'use strict';
describe('random id generator',function () {
  const assert = require('assert');
  it('randomIdGenerator should be a function', function () {
    let randomIdGenerator = require('../lib/index.js');
    assert.equal(typeof randomIdGenerator,'function')
  });
  it('randomIdGenerator should return an Id, which have a length of 6', function(){
    let randomIdGenerator = require('../lib/index.js');
    let id = randomIdGenerator();
    console.log(id);
    assert.equal(id.length, 6);
  });
  it('randomIdGenerator should return an Id, which have a length of specified value', function(){
    let randomIdGenerator = require('../lib/index.js');
    let length = 10;
    let id = randomIdGenerator(length);
    console.log(id);
    assert.equal(id.length, length);
  });
});
